mapper.select:
select

from t_emc_auth_employee
where 1=1 AND STATUS ='0'

   and ORG_ID = #{orgId,jdbcType=BIGINT}


    and  EMP_NAME = #{empName,jdbcType=VARCHAR}


    and  SEX = #{sex,jdbcType=BIT}


    and  AGE = #{age,jdbcType=BIT}


    and BIRTHDAY = #{birthday,jdbcType=VARCHAR}


    and JOB_NUM = #{jobNum,jdbcType=VARCHAR}


    and  TEL = #{tel,jdbcType=VARCHAR}


    and  PHONE = #{phone,jdbcType=VARCHAR}


    and  EMAIL = #{email,jdbcType=VARCHAR}


    and  MEMO = #{memo,jdbcType=VARCHAR}


    and  CREATOR = #{creator,jdbcType=VARCHAR}


    and  CREATE_TIME = #{createTime,jdbcType=VARCHAR}

mapper.insert:
insert into t_emc_auth_employee (ID, ORG_ID, EMP_NAME,
SEX, AGE, BIRTHDAY, JOB_NUM,
TEL, PHONE, EMAIL,
MEMO, CREATOR, CREATE_TIME,STATUS
)
values (#{id,jdbcType=BIGINT}, #{orgId,jdbcType=BIGINT}, #{empName,jdbcType=VARCHAR},
#{sex,jdbcType=BIT}, #{age,jdbcType=BIT}, #{birthday,jdbcType=VARCHAR}, #{jobNum,jdbcType=VARCHAR},
#{tel,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR},
#{memo,jdbcType=VARCHAR}, #{creator,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}, #{status,jdbcType=BIT}
)

mapper.delete:
delete from t_emc_auth_employee
where ID in
 #{id}
