mapper.insert:
insert into t_emc_org_oncenet (NET_TYPE_ID, LENGTH,
  CELL_NUM, PART_NUM, MEDIUM
  )
values (#{netTypeId,jdbcType=VARCHAR}, #{length,jdbcType=DOUBLE},
  #{cellNum,jdbcType=INTEGER}, #{partNum,jdbcType=INTEGER}, #{medium,jdbcType=VARCHAR}
  )

mapper.select:
select

from t_emc_org_oncenet
where 1=1

    and  NET_TYPE_ID =  #{cellNum,jdbcType=TINYINT}


    and CELL_NUM = #{cellNum,jdbcType=TINYINT}

order by id
