mapper.insert:
insert into t_emc_unit_line (ID,LINE_NAME,LINE_CODE, NET_TYPE_ID, LENGTH,
CELL_NUM, PART_NUM, MEDIUM,ORG_ID,COM_ID
)
values (#{id,jdbcType=VARCHAR},#{lineName,jdbcType=VARCHAR},#{lineCode,jdbcType=VARCHAR}, #{netTypeId,jdbcType=TINYINT}, #{length,jdbcType=DOUBLE},
#{cellNum,jdbcType=INTEGER}, #{partNum,jdbcType=INTEGER}, #{medium,jdbcType=VARCHAR} ,#{orgId,jdbcType=BIGINT} ,#{comId,jdbcType=VARCHAR}
)

mapper.select:
select
ID,
LINE_NAME,
LINE_CODE,
NET_TYPE_ID,
LENGTH,
CELL_NUM,
PART_NUM,
MEDIUM,
ORG_ID,
COM_ID
from t_emc_unit_line
where 1=1

    and  LINE_NAME =  #{lineName,jdbcType=VARCHAR}


    and  LINE_CODE =  #{lineCode,jdbcType=VARCHAR}



    and  NET_TYPE_ID =  #{netTypeId,jdbcType=TINYINT}



    and  LENGTH = #{length,jdbcType=DOUBLE}


    and CELL_NUM = #{cellNum,jdbcType=INTEGER}


    and  PART_NUM = #{partNum,jdbcType=INTEGER}


    and  MEDIUM = #{medium,jdbcType=VARCHAR}


    and  ORG_ID = #{orgId,jdbcType=BIGINT}


    and  COM_ID = #{comId,jdbcType=VARCHAR}

order by id

select
ID,
LINE_NAME,
LINE_CODE,
(CASE NET_TYPE_ID WHEN 1 THEN '干线' WHEN 2 THEN '支线' WHEN 3 THEN '联通线' WHEN 4 THEN '户线' ELSE '' END) NET_TYPE_NAME,
LENGTH,
CELL_NUM,
PART_NUM,
MEDIUM,
ORG_ID,
COM_ID
from t_emc_unit_line
where 1=1


    and  LINE_NAME =  #{netName,jdbcType=VARCHAR}


    and  LINE_CODE =  #{netCode,jdbcType=VARCHAR}



    and  NET_TYPE_ID =  #{netTypeId,jdbcType=TINYINT}



    and  LENGTH = #{length,jdbcType=DOUBLE}


    and CELL_NUM = #{cellNum,jdbcType=INTEGER}


    and  PART_NUM = #{partNum,jdbcType=INTEGER}


    and  MEDIUM = #{medium,jdbcType=VARCHAR}


    and  ORG_ID = #{orgId,jdbcType=BIGINT}


    and  COM_ID = #{comId,jdbcType=VARCHAR}

order by id
