mapper.select:
SELECT ROUND(SUM(a.BM),1) from ( SELECT sum(h.BM) BM,h.TYPE,ou.UNITTYPE from ( SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR})) and o.COM_ID=#{comId,jdbcType=VARCHAR}) c,v_emc_unit v
WHERE c.ID=v.ORGID and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} GROUP BY v.UNITID,v.UNITTYPE ) ou LEFT JOIN (SELECT fd.UNITID,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE FROM ${tableName} fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID WHERE 1=1
AND fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
AND fd.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
and et.TYPE!=4 GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID GROUP BY h.TYPE,ou.UNITTYPE ) a

SELECT
ROUND(sum(t.DOSAGE*t.C_COEF),1) AS CarbonTotal

FROM
${tableName} t , v_emc_unit v,
(SELECT id FROM t_emc_org where FIND_IN_SET(  id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))) a
WHERE
v.COMID=t.COMID AND
v.unitid=t.UNITID AND
v.orgid=a.id

    and v.COMID=#{comId,jdbcType=VARCHAR}


    and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}


    and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

select ROUND(max(case when cc.TYPEID = '1' then cc.cost else 0 end),1) manage, ROUND(max(case when cc.TYPEID = '2' then cc.cost else 0 end),1) other, ROUND(max(case when cc.TYPEID = '3' then cc.cost else 0 end),1) device, ROUND(max(case when cc.TYPEID = '4' then cc.cost else 0 end),1) labor, (select sum(bb.ne) from (SELECT a.typeid, sum(a.DOSAGE*a.PRICE) ne FROM ${tableName} a , (select v.COMID,v.UNITID from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c WHERE a.COMID = c.COMID AND a.UNITID = c.unitid
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
and c.COMID=#{comId,jdbcType=VARCHAR}
GROUP BY TYPEID)bb)energy
from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select v.COMID,v.UNITID from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c where b.COMID = c.comid and b.UNITID = c.unitid
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
and c.COMID=#{comId,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc

SELECT ROUND(z.zbm/z.area,2)  DH from (
SELECT  sum(b.area) area,
(SELECT SUM(a.bm) from (
SELECT  sum(h.BM) BM,sum(h.DOSAGE) DOSAGE,h.TYPE,ou.UNITTYPE,SUM(h.AREA) AREA  from (
SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM
t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))


    and o.COM_ID=#{comId,jdbcType=VARCHAR}

) c,v_emc_unit v

WHERE c.ID=v.ORGID
and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}

    and v.UNITTYPE = #{orgType,jdbcType=VARCHAR}

GROUP BY v.UNITID,v.UNITTYPE
) ou
LEFT JOIN
(SELECT fd.UNITID,SUM(fd.DOSAGE) DOSAGE,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE,AVG(fd.AREA) AREA
FROM ${tableName} fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID
WHERE 1=1

    AND fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    AND fd.DOSAGE_TIME  <= #{endTime,jdbcType=VARCHAR}

and et.TYPE!=4
GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID
GROUP BY h.TYPE,ou.UNITTYPE
)a) zbm from (
SELECT  a.UNITTYPE , avg(a.AREA) area from (
SELECT  sum(h.BM) BM,sum(h.DOSAGE) DOSAGE,h.TYPE,ou.UNITTYPE,SUM(h.AREA) AREA  from (
SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM
t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))


    and o.COM_ID=#{comId,jdbcType=VARCHAR}


) c,v_emc_unit v
WHERE c.ID=v.ORGID
and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}

    and v.UNITTYPE = #{orgType,jdbcType=VARCHAR}

GROUP BY v.UNITID,v.UNITTYPE
) ou
LEFT JOIN
(SELECT fd.UNITID,SUM(fd.DOSAGE) DOSAGE,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE,AVG(fd.AREA) AREA
FROM ${tableName} fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID
WHERE 1=1

    AND fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    AND fd.DOSAGE_TIME  <= #{endTime,jdbcType=VARCHAR}

and et.TYPE!=4
GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID
GROUP BY h.TYPE,ou.UNITTYPE
) a GROUP BY a.UNITTYPE
)b
) z

SELECT ROUND(sum(b.area)/10000,4) area from (
    SELECT  a.UNITTYPE , avg(a.AREA) area from (
    SELECT  sum(h.BM) BM,sum(h.DOSAGE) DOSAGE,h.TYPE,ou.UNITTYPE,SUM(h.AREA) AREA  from (
    SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM
t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))


    and o.COM_ID=#{comId,jdbcType=VARCHAR}


) c,v_emc_unit v
WHERE c.ID=v.ORGID
and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}

    and v.UNITTYPE = #{orgType,jdbcType=VARCHAR}

GROUP BY v.UNITID,v.UNITTYPE
) ou
LEFT JOIN
(SELECT fd.UNITID,SUM(fd.DOSAGE) DOSAGE,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE,AVG(fd.AREA) AREA
FROM ${tableName} fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID
WHERE  1=1

AND fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    AND fd.DOSAGE_TIME  <= #{endTime,jdbcType=VARCHAR}

and et.TYPE!=4
GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID
GROUP BY h.TYPE,ou.UNITTYPE
) a GROUP BY a.UNITTYPE )b

SELECT ROUND(sum(b.fe),1) from ( select sum(a.DOSAGE*a.C_COEF) as fe,a.typeid from ${tableName} a , (select * from v_emc_unit where UNITTYPE='1' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c, t_emc_energy_type e where a.COMID = c.COMID AND a.UNITID = c.unitid and a.TYPEID = e.id
and c.COMID=#{comId,jdbcType=VARCHAR}
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.TYPEID) b

select ROUND(max(case when cc.TYPEID = '1' then cc.cost else 0 end),1) manage, ROUND(max(case when cc.TYPEID = '2' then cc.cost else 0 end),1) other, ROUND(max(case when cc.TYPEID = '3' then cc.cost else 0 end),1) device, ROUND(max(case when cc.TYPEID = '4' then cc.cost else 0 end),1) labor, (select sum(bb.ne) from (SELECT a.typeid, sum(a.DOSAGE*a.PRICE) ne FROM ${tableName} a , (select * from v_emc_unit v where UNITTYPE='1' and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c WHERE a.COMID = c.COMID AND a.UNITID = c.unitid
and c.COMID=#{comId,jdbcType=VARCHAR}
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY TYPEID)bb)energy
from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit v where UNITTYPE='1' and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c where b.COMID = c.comid and b.UNITID = c.unitid
and c.COMID=#{comId,jdbcType=VARCHAR}
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc

SELECT ROUND(sum(t.LENGTH),2) from t_emc_unit_net t where t.ORG_ID in (SELECT id FROM t_emc_org WHERE FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ))
and t.COM_ID=#{comId,jdbcType=VARCHAR}
and t.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}

select ROUND(max(case when cc.TYPEID = '1' then cc.cost else 0 end),1) manage, ROUND(max(case when cc.TYPEID = '2' then cc.cost else 0 end),1) other, ROUND(max(case when cc.TYPEID = '3' then cc.cost else 0 end),1) device, ROUND(max(case when cc.TYPEID = '4' then cc.cost else 0 end),1) labor, '0' energy from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit where UNITTYPE='2' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c, t_emc_unit_net n where b.COMID = c.comid and b.UNITID = c.unitid and n.id = c.unitid
and c.COMID=#{comId,jdbcType=VARCHAR}
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc

