SELECT sum(n.LENGTH) from t_emc_unit_line n where n.ORG_ID in (SELECT id FROM t_emc_org WHERE FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ))
and n.COM_ID=#{comId,jdbcType=VARCHAR}
and n.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}

select ROUND(max(case when cc.TYPEID = '1' then cc.cost else 0 end),1) manage, ROUND(max(case when cc.TYPEID = '2' then cc.cost else 0 end),1) other, ROUND(max(case when cc.TYPEID = '3' then cc.cost else 0 end),1) device, ROUND(max(case when cc.TYPEID = '4' then cc.cost else 0 end),1) labor, '0' energy from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit where UNITTYPE='4' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c, t_emc_unit_line n where b.COMID = c.comid and b.UNITID = c.unitid and n.id = c.unitid
and c.COMID=#{comId,jdbcType=VARCHAR}
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
