SELECT ROUND(sum(b.fe),1) from ( select sum(a.DOSAGE*a.COAL_COEF) as fe,a.typeid from ${tableName} a , (select * from v_emc_unit v where v.UNITTYPE='5' and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c, t_emc_energy_type e
where a.COMID = c.COMID AND a.UNITID = c.unitid and a.TYPEID = e.id
and c.COMID=#{comId,jdbcType=VARCHAR}
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
