mapper.select:
SELECT ROUND(SUM(a.BM),1) from ( SELECT sum(h.BM) BM,sum(h.DOSAGE) DOSAGE,h.TYPE,ou.UNITTYPE,SUM(h.AREA) AREA from ( SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR})) and o.COM_ID=#{comId,jdbcType=VARCHAR}) c,v_emc_unit v
WHERE c.ID=v.ORGID and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}
GROUP BY v.UNITID,v.UNITTYPE ) ou LEFT JOIN (SELECT fd.UNITID,SUM(fd.DOSAGE) DOSAGE,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE,AVG(fd.AREA) AREA FROM t_emc_final_data_hour fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID WHERE fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR} AND fd.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR} and et.id !=3 GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID GROUP BY h.TYPE,ou.UNITTYPE ) a

SELECT ROUND(sum(t.DOSAGE*t.C_COEF),1) AS CarbonTotal
FROM t_emc_final_data_hour t , v_emc_unit v, (SELECT id FROM t_emc_org where FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))) a WHERE v.COMID=t.COMID AND v.unitid=t.UNITID AND v.orgid=a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

select ROUND(max(case when cc.TYPEID = '1' then cc.cost else 0 end),1) manage, ROUND(max(case when cc.TYPEID = '2' then cc.cost else 0 end),1) other, ROUND(max(case when cc.TYPEID = '3' then cc.cost else 0 end),1) device, ROUND(max(case when cc.TYPEID = '4' then cc.cost else 0 end),1) labor, (select sum(bb.ne) from (SELECT a.typeid, sum(a.DOSAGE*a.PRICE) ne FROM t_emc_final_data_hour a , (select * from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c WHERE a.COMID = c.COMID AND a.UNITID = c.unitid
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY TYPEID)bb)ENERGY
from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c where b.COMID = c.comid and b.UNITID = c.unitid
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc

SELECT ROUND(SUM(a.BM)/AVG(a.AREA),1) DH from ( SELECT sum(h.BM) BM,sum(h.DOSAGE) DOSAGE,h.TYPE,ou.UNITTYPE,SUM(h.AREA) AREA from ( SELECT v.UNITTYPE,v.UNITID from (SELECT ID FROM t_emc_org o WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR})) and o.COM_ID=#{comId,jdbcType=VARCHAR}) c,v_emc_unit v
WHERE c.ID=v.ORGID and v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR}
GROUP BY v.UNITID,v.UNITTYPE ) ou LEFT JOIN (SELECT fd.UNITID,SUM(fd.DOSAGE) DOSAGE,SUM(fd.DOSAGE*fd.COAL_COEF) BM,et.TYPE,AVG(fd.AREA) AREA FROM t_emc_final_data_hour fd LEFT JOIN t_emc_energy_type et on fd.TYPEID=et.ID WHERE fd.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR} AND fd.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR} and et.id !=3 GROUP BY fd.UNITID,et.TYPE) h on ou.UNITID=h.UNITID GROUP BY h.TYPE,ou.UNITTYPE ) a

SELECT ROUND(sum(c.AVG_AREA)/10000,1) as Price_Area FROM ( SELECT a.id, SUM(t.DOSAGE*t.COAL_COEF) as bmzl, AVG(t.AREA) AS AVG_AREA FROM t_emc_final_data_hour t, v_emc_unit v, ( SELECT id FROM t_emc_org WHERE FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ) ) a WHERE v.COMID = t.COMID AND v.unitid = t.UNITID AND v.orgid = a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.id ) c

SELECT ROUND(sum(b.fe),1) from ( select sum(a.DOSAGE*a.COAL_COEF) as fe,a.typeid from t_emc_final_data_hour a , (select * from v_emc_unit where UNITTYPE='1' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c, t_emc_energy_type e
where a.COMID = c.COMID AND a.UNITID = c.unitid and a.TYPEID = e.id
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.TYPEID) b

SELECT ROUND(sum(b.fe),1) from ( select sum(a.DOSAGE*a.C_COEF) as fe,a.typeid from t_emc_final_data_hour a , (select * from v_emc_unit where UNITTYPE='1' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c, t_emc_energy_type e where a.COMID = c.COMID AND a.UNITID = c.unitid and a.TYPEID = e.id
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
