SELECT sum(t.DOSAGE*t.COAL_COEF) AS Etotal FROM t_emc_final_data_hour t , v_emc_unit v, (SELECT id FROM t_emc_org where FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))) a WHERE v.COMID=t.COMID AND v.unitid=t.UNITID AND v.orgid=a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

SELECT sum(t.DOSAGE*t.C_COEF) AS CarbonTotal
FROM t_emc_final_data_hour t , v_emc_unit v, (SELECT id FROM t_emc_org where FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}))) a WHERE v.COMID=t.COMID AND v.unitid=t.UNITID AND v.orgid=a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

select max(case when cc.TYPEID = '1' then cc.cost else 0 end) manage, max(case when cc.TYPEID = '2' then cc.cost else 0 end) other, max(case when cc.TYPEID = '3' then cc.cost else 0 end) device, max(case when cc.TYPEID = '4' then cc.cost else 0 end) labor, (select sum(bb.ne) from (SELECT a.typeid, sum(a.DOSAGE*a.PRICE) ne FROM t_emc_final_data_hour a , (select * from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c WHERE a.COMID = c.COMID AND a.UNITID = c.unitid
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY TYPEID)bb)ENERGY
from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit v where v.HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and v.ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c where b.COMID = c.comid and b.UNITID = c.unitid
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc

SELECT sum(c.bmzl)/sum(c.AVG_AREA) as Yardage FROM ( SELECT a.id, SUM(t.DOSAGE*t.COAL_COEF) as bmzl, AVG(t.AREA) AS AVG_AREA FROM t_emc_final_data_hour t, v_emc_unit v, ( SELECT id FROM t_emc_org WHERE FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ) ) a WHERE v.COMID = t.COMID AND v.unitid = t.UNITID AND v.orgid = a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.id ) c

SELECT sum(c.AVG_AREA)/10000 as Price_Area FROM ( SELECT a.id, SUM(t.DOSAGE*t.COAL_COEF) as bmzl, AVG(t.AREA) AS AVG_AREA FROM t_emc_final_data_hour t, v_emc_unit v, ( SELECT id FROM t_emc_org WHERE FIND_IN_SET( id, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ) ) a WHERE v.COMID = t.COMID AND v.unitid = t.UNITID AND v.orgid = a.id
and v.HEAT_TYPE = #{feetType,jdbcType=VARCHAR}
and t.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and t.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.id ) c

SELECT sum(b.fe) from ( select sum(a.DOSAGE*a.COAL_COEF) as fe,a.typeid from t_emc_final_data_hour a , (select * from v_emc_unit where UNITTYPE='1' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) ) c, t_emc_energy_type e
where a.COMID = c.COMID AND a.UNITID = c.unitid and a.TYPEID = e.id
and a.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and a.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY a.TYPEID) b

