SELECT (m.labor+m.device+m.manage+m.other) as gg from (
select max(case when cc.TYPEID = '1' then cc.cost else 0 end) manage, max(case when cc.TYPEID = '2' then cc.cost else 0 end) other, max(case when cc.TYPEID = '3' then cc.cost else 0 end) device, max(case when cc.TYPEID = '4' then cc.cost else 0 end) labor, '0' ENERGY from (SELECT b.TYPEID, sum(b.COST) cost
FROM t_emc_final_cost_day b, (select * from v_emc_unit where UNITTYPE='2' and HEAT_TYPE=#{feetType,jdbcType=VARCHAR} and ORGID in (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId,jdbcType=VARCHAR}))) )c, t_emc_unit_net n where b.COMID = c.comid and b.UNITID = c.unitid and n.id = c.unitid
and b.COST_DATE >= #{startTime,jdbcType=VARCHAR}
and b.COST_DATE <= #{endTime,jdbcType=VARCHAR}
GROUP BY b.TYPEID) cc ) m

