mapper.select:
	select 

  from t_emc_auth_user
  where 1=1
   and USER_NAME LIKE CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
   and LOGIN LIKE CONCAT('%',#{userAccount,jdbcType=VARCHAR},'%')
	 and USE_STATUS = #{userStatus,jdbcType=VARCHAR}
	 and CREATOR LIKE CONCAT('%',#{creator,jdbcType=VARCHAR},'%')
	 and CREATE_TIME >= #{cStartTime,jdbcType=VARCHAR}
	 and CREATE_TIME <= #{cEndTime,jdbcType=VARCHAR}
	 and LOGIN_TIME >= #{lStartTime,jdbcType=VARCHAR}
	 and LOGIN_TIME <= #{lEndTime,jdbcType=VARCHAR}

	select 

  from t_emc_auth_user
  where 1=1
   and USER_NAME LIKE CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
   and LOGIN LIKE CONCAT('%',#{userAccount,jdbcType=VARCHAR},'%')
	 and USE_STATUS = #{userStatus,jdbcType=VARCHAR}
	 and CREATOR LIKE CONCAT('%',#{creator,jdbcType=VARCHAR},'%')
	 and CREATE_TIME >= #{cStartTime,jdbcType=VARCHAR}
	 and CREATE_TIME <= #{cEndTime,jdbcType=VARCHAR}
	 and LOGIN_TIME >= #{lStartTime,jdbcType=VARCHAR}
	 and LOGIN_TIME <= #{lEndTime,jdbcType=VARCHAR}

