mapper.select:
SELECT v.fgsid id,v.ORG_NAME orgname, ROUND(IFNULL(SUM(v.BQBM),0),2) BQBM, ROUND(IFNULL(SUM(v.TQBM),0),2) TQBM, ROUND(MAX(CASE v.TYPE WHEN '1' THEN v.BQ ELSE 0 END),2) bqwater, ROUND(MAX(CASE v.TYPE WHEN '1' THEN v.TQ ELSE 0 END),2) tqwater, ROUND(MAX(CASE v.TYPE WHEN '2' THEN v.BQ ELSE 0 END),2) bqelectric, ROUND(MAX(CASE v.TYPE WHEN '2' THEN v.TQ ELSE 0 END),2) tqelectric, ROUND(MAX(CASE v.TYPE WHEN '3' THEN v.BQ ELSE 0 END),2) bqgas, ROUND(MAX(CASE v.TYPE WHEN '3' THEN v.TQ ELSE 0 END),2) tqgas, ROUND(MAX(CASE v.TYPE WHEN '4' THEN v.BQ ELSE 0 END),2) bqheat, ROUND(MAX(CASE v.TYPE WHEN '4' THEN v.TQ ELSE 0 END),2) tqheat, ROUND(MAX(CASE v.TYPE WHEN '5' THEN v.BQ ELSE 0 END),2) bqcoal, ROUND(MAX(CASE v.TYPE WHEN '5' THEN v.TQ ELSE 0 END),2) tqcoal, ROUND(MAX(CASE v.TYPE WHEN '6' THEN v.BQ ELSE 0 END),2) bqoil, ROUND(MAX(CASE v.TYPE WHEN '6' THEN v.TQ ELSE 0 END),2) tqoil FROM ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.TQ,f.BQBM,f.TQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(hd.BQBM) BQBM,SUM(hd.TQBM) TQBM,SUM(hd.BQ) BQ,SUM(hd.TQ) TQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit_feed vuf ON vuf.ORG_ID = a.orgid AND vuf.feedtype=#{feedType} LEFT JOIN ( SELECT d1.UNITID,d1.TYPEID,d1.DOSAGE BQ,d1.BM BQBM,d2.DOSAGE TQ,d2.BM TQBM FROM (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1, (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTimeTq} AND fdh.DOSAGE_TIME <= #{endTimeTq} GROUP BY fdh.UNITID,fdh.TYPEID) d2 WHERE d1.UNITID = d2.UNITID AND d1.TYPEID=d2.TYPEID ) hd ON hd.UNITID = vuf.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = hd.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v GROUP BY v.fgsid,v.ORG_NAME

SELECT v.fgsid id,v.ORG_NAME orgname, ROUND(IFNULL(SUM(v.BQBM),0),2) BQBM FROM ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(hd.BQBM) BQBM,SUM(hd.BQ) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit_feed vuf ON vuf.ORG_ID = a.orgid AND vuf.feedtype=#{feedType} LEFT JOIN ( SELECT d1.UNITID,d1.TYPEID,d1.DOSAGE BQ,d1.BM BQBM FROM (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ) hd ON hd.UNITID = vuf.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = hd.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v GROUP BY v.fgsid,v.ORG_NAME
