SELECT v.fgsid id,v.ORG_NAME orgname, ROUND(IFNULL(SUM(v.BQBM),0),2) BQBM, ROUND(IFNULL(SUM(v1.BQBM),0),2) TQBM, ROUND(MAX(CASE v.TYPE WHEN '1' THEN v.BQ ELSE 0 END),2) bqwater, ROUND(MAX(CASE v.TYPE WHEN '2' THEN v.BQ ELSE 0 END),2) bqelectric, ROUND(MAX(CASE v.TYPE WHEN '3' THEN v.BQ ELSE 0 END),2) bqgas, ROUND(MAX(CASE v.TYPE WHEN '4' THEN v.BQ ELSE 0 END),2) bqheat, ROUND(MAX(CASE v.TYPE WHEN '5' THEN v.BQ ELSE 0 END),2) bqcoal, ROUND(MAX(CASE v.TYPE WHEN '6' THEN v.BQ ELSE 0 END),2) bqoil, ROUND(MAX(CASE v1.TYPE WHEN '1' THEN v1.BQ ELSE 0 END),2) tqwater, ROUND(MAX(CASE v1.TYPE WHEN '2' THEN v1.BQ ELSE 0 END),2) tqelectric, ROUND(MAX(CASE v1.TYPE WHEN '3' THEN v1.BQ ELSE 0 END),2) tqgas, ROUND(MAX(CASE v1.TYPE WHEN '4' THEN v1.BQ ELSE 0 END),2) tqheat, ROUND(MAX(CASE v1.TYPE WHEN '5' THEN v1.BQ ELSE 0 END),2) tqcoal, ROUND(MAX(CASE v1.TYPE WHEN '6' THEN v1.BQ ELSE 0 END),2) tqoil FROM ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ON d1.UNITID = vu.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = d1.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v LEFT JOIN ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTimeTq} AND fdh.DOSAGE_TIME <= #{endTimeTq} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ON d1.UNITID = vu.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = d1.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v1 ON v.fgsid = v1.fgsid AND v.TYPE = v1.TYPE GROUP BY v.fgsid,v.ORG_NAME

SELECT f.fgsid id,f.ORG_NAME name, ROUND(IFNULL(SUM(f.BQBM),0),2) value FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ON d1.UNITID = vu.unitid GROUP BY a.fgsid,a.ORG_NAME) f GROUP BY f.fgsid,f.ORG_NAME

SELECT a.FGSID,a.ORG_NAME NAME,ROUND(IFNULL(SUM(hd.BQBM),0),2) BQBM,dt.DOSAGE_TIME DATE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM (
SELECT
( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid,
o.*
FROM
t_emc_org o
WHERE o.COM_ID = #{comId}) t,
(SELECT * FROM t_emc_org WHERE 1=1

    AND P_ORG_ID = #{pOrgId}


    AND ID = #{orgId}

) tmo
WHERE tmo.ID = t.fgsid) a
RIGHT JOIN (SELECT DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour  WHERE DOSAGE_TIME >=  #{startTime} AND DOSAGE_TIME <=  #{endTime} GROUP BY DATE_FORMAT(DOSAGE_TIME,'%y-%m-%d') ORDER BY DOSAGE_TIME) dt ON 1=1
LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}

    AND vu.UNITTYPE=#{orgType}

LEFT JOIN  (
SELECT d1.UNITID,SUM(d1.BM) BQBM,d1.DOSAGE_TIME FROM
(SELECT fdh.UNITID,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID,DATE_FORMAT(fdh.DOSAGE_TIME,'%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >=  #{startTime} AND fdh.DOSAGE_TIME <=  #{endTime}
GROUP BY fdh.UNITID,fdh.DOSAGE_TIME) d1 GROUP BY d1.UNITID,d1.DOSAGE_TIME ORDER BY DOSAGE_TIME
) hd ON hd.UNITID = vu.unitid AND hd.DOSAGE_TIME = dt.DOSAGE_TIME
GROUP BY a.fgsid,a.ORG_NAME,dt.DOSAGE_TIME
ORDER BY a.fgsid,dt.DOSAGE_TIME

SELECT v.fgsid ID,v.ORG_NAME ORGNAME, ROUND(IFNULL(SUM(v.BQBM),0),2) BQBM, ROUND(IFNULL(SUM(v1.BQBM),0),2) TQBM FROM ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ON d1.UNITID = vu.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = d1.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v LEFT JOIN ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTimeTq} AND fdh.DOSAGE_TIME <= #{endTimeTq} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ON d1.UNITID = vu.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = d1.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v1 ON v.fgsid = v1.fgsid AND v.TYPE = v1.TYPE GROUP BY v.fgsid,v.ORG_NAME

SELECT v.fgsid ID,v.ORG_NAME NAME, ROUND(IFNULL(SUM(v.BQBM),0),2) VALUE FROM ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ,f.BQBM FROM (SELECT a.fgsid,a.ORG_NAME,SUM(hd.BQBM) BQBM,SUM(hd.BQ) BQ,eet.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND P_ORG_ID = #{pOrgId}
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN ( SELECT d1.UNITID,d1.TYPEID,d1.DOSAGE BQ,d1.BM BQBM FROM (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,fdh.TYPEID FROM t_emc_final_data_hour fdh WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,fdh.TYPEID) d1 ) hd ON hd.UNITID = vu.unitid LEFT JOIN t_emc_energy_type eet ON eet.ID = hd.TYPEID GROUP BY a.fgsid,a.ORG_NAME,eet.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v GROUP BY v.fgsid,v.ORG_NAME ORDER BY IFNULL(SUM(v.BQBM),0) DESC
