mapper.update:
update t_emc_health_score_record
set ORG_ID = #{orgId,jdbcType=VARCHAR},
  SCORE = #{score,jdbcType=DOUBLE},
  USERID = #{userid,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT

from t_emc_health_score_record sr
where sr.ORG_ID=#{orgId,jdbcType=VARCHAR}
and sr.USERID=#{userid,jdbcType=VARCHAR}
ORDER BY sr.CREATE_TIME desc LIMIT 1

SELECT a.UNITID,a.UNITNAME,b.type TYPE,b.dh DH,c.NAME,c.ENTERPRISE,c.LOCAL,c.INDUSTRY,c.UNIT_METER from (SELECT v.UNITID,v.UNITNAME,v.HEAT_TYPE,v.UNITTYPE,v.COMID FROM (SELECT ID FROM t_emc_org WHERE FIND_IN_SET ( ID, emc_func_org_getchilds (#{orgId}))) o,v_emc_unit v WHERE o.ID=v.ORGID AND COMID=#{comId} ) a LEFT JOIN (SELECT b.type, b.unitid, ROUND(AVG(b.dosage) / (avg(b.area)) ,2) dh FROM ( SELECT a.UNITID unitid, b.type type, DATE_FORMAT(a.DOSAGE_TIME,'%Y-%m-%d %H') time, avg(a.AREA) area, avg(a.DOSAGE) dosage FROM t_emc_final_data_hour_tj a LEFT JOIN t_emc_energy_type b ON a.TYPEID = b.id WHERE 1=1
and a.COMID = #{comId}
GROUP BY a.UNITID, b.type, DATE_FORMAT(a.DOSAGE_TIME,'%Y-%m-%d %H') ) b GROUP BY b.type, b.UNITID )b on a.UNITID=b.unitid LEFT JOIN ( SELECT f.NAME, e.UNIT_ID, f.ENERGY_TYPE, e.ENTERPRISE, f.UNIT_TYPE, f.UNIT_METER, e. LOCAL, e.INDUSTRY FROM t_emc_index_record e LEFT JOIN t_emc_index_type f ON e.TYPE_ID = f.ID WHERE e.INDEX_TIME = ( SELECT max(INDEX_TIME) FROM t_emc_index_record WHERE UNIT_ID = e.UNIT_ID AND TYPE_ID = e.TYPE_ID GROUP BY UNIT_ID, TYPE_ID )
and e.COM_ID = #{comId}
group by f.ENERGY_TYPE,e.UNIT_ID) c on b.unitid=c.UNIT_ID
where c.NAME=#{name}

SELECT DISTINCT
    tj.id,tj.STATION_NAME,tj.TEMP,tj.ROMM_CODE,tj.COMMUNITY_NAME,tj.REDE_TIME,t.MAXTEMP,t.MINTEMP
FROM
    t_emc_temperature_tj tj,t_emc_alarm_config_temp t
where
    tj.COM_ID = t.COM_ID

and tj.REDE_TIME >=  #{startTime}
and tj.REDE_TIME <=  #{endTime}
and tj.COM_ID = #{comId}
