SELECT
d.UNITNAME as unitName,
d.UNITTYPE as unitType,

    MAX((CASE d.DOSAGE_TIME WHEN #{tag} THEN d.DOSAGE/d.AREA ELSE 0 END)) c${index}

,

    MAX((CASE d.DOSAGE_TIME WHEN #{val} THEN d.DOSAGE/d.AREA ELSE 0 END)) l${i}

,

    0 p${i}

FROM (select a.unitid,a.unittype,a.unitname,m.DOSAGE as DOSAGE,m.DOSAGE_TIME as DOSAGE_TIME ,m.BM,m.AREA  FROM
(select UNITID,UNITNAME,UNITTYPE from v_emc_unit where 1=1

    and  HEAT_TYPE =#{feedType}

UNITTYPE = #{orgType}

    and FIND_IN_SET(ORGID,emc_func_org_getchilds(#{orgId}))

)a left join
( SELECT AVG(fdh.AREA) AREA,fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d') DOSAGE_TIME
FROM t_emc_final_data_hour_tj fdh,t_emc_energy_type eet  WHERE 1=1

    AND fdh.COMID= #{comId}


    and eet.TYPE = #{energytype,jdbcType=VARCHAR}



    and fdh.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

and fdh.TYPEID = eet.ID
GROUP BY fdh.UNITID,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d')) m on a.unitid = m.unitid
union all
(select a.unitid,a.unittype,a.unitname,m.DOSAGE as DOSAGE,m.DOSAGE_TIME as DOSAGE_TIME ,m.BM,m.AREA  FROM
(select UNITID,UNITNAME,UNITTYPE from v_emc_unit where 1=1

    and  HEAT_TYPE =#{feedType}

HEAT_TYPE = #{orgType}

    and FIND_IN_SET(ORGID,emc_func_org_getchilds(#{orgId}))

)a left join
(SELECT AVG(fdh.AREA) AREA,fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d')
DOSAGE_TIME
FROM t_emc_final_data_hour_tj fdh,t_emc_energy_type eet WHERE fdh.TYPEID = eet.ID
AND fdh.COMID= #{comId} 
and eet.TYPE = #{energytype,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME >= #{startTimeTq,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME <= #{endTimeTq,jdbcType=VARCHAR}

GROUP BY fdh.UNITID,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d'))m on a.unitid = m.unitid
)

) d   GROUP BY d.UNITID  order by d.UNITTYPE

SELECT (CASE WHEN M.UNITTYPE = '1' THEN '源' WHEN M.UNITTYPE = '2' THEN '网' WHEN M.UNITTYPE = '3' THEN '站' WHEN M.UNITTYPE = '4' THEN '线' WHEN M.UNITTYPE = '5' THEN '户' else '' end) as unitName, M.UNITTYPE as unitType,
ROUND(IFNULL(MAX((CASE M.DOSAGE_TIME WHEN #{tag} THEN M.DOSAGE/M.area ELSE 0 END)),0),2) c${index}
,
ROUND(IFNULL(MAX((CASE M.DOSAGE_TIME WHEN #{val} THEN M.DOSAGE/M.area ELSE 0 END)),0),2) l${i}
,
0 p${i}
FROM (SELECT ev.UNITTYPE, SUM(fdh.DOSAGE) DOSAGE, avg(fdh.area) area, SUM(fdh.DOSAGE * fdh.COAL_COEF) BM, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour_tj fdh, t_emc_energy_type eet, (select UNITID,UNITTYPE,COMID from v_emc_unit where 1=1
and EXISTS (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId})) and id = ORGID)
) ev WHERE fdh.TYPEID = eet.ID and fdh.UNITID = ev.UNITID and fdh.COMID = ev.COMID
AND fdh.COMID= #{comId}
and eet.TYPE = #{type,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY ev.UNITTYPE, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d')
UNION ALL (SELECT ev.UNITTYPE, avg(fdh.area) area, SUM(fdh.DOSAGE) DOSAGE, SUM(fdh.DOSAGE * fdh.COAL_COEF) BM, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour_tj fdh, t_emc_energy_type eet, (select UNITID,UNITTYPE,COMID from v_emc_unit where 1=1
and EXISTS (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId})) and id = ORGID)
) ev WHERE fdh.TYPEID = eet.ID and fdh.UNITID = ev.UNITID and fdh.COMID = ev.COMID
AND fdh.COMID= #{comId}
and eet.TYPE = #{type,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME >= #{startTimeTq,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME <= #{endTimeTq,jdbcType=VARCHAR}
GROUP BY ev.UNITTYPE, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d')))M
group by M.UNITTYPE

SELECT v.fgsid id,v.DOSAGE_TIME TIME, ROUND(IFNULL(SUM(CASE v.TYPE WHEN '4' THEN 0 ELSE v.BQBM END)/AVG(v.BQAREA),0),2) BQDH, ROUND(IFNULL(SUM(CASE v1.TYPE WHEN '4' THEN 0 ELSE v1.BQBM END)/AVG(v1.BQAREA),0),2) TQDH FROM ( SELECT f.sort,f.fgsid,f.ORG_NAME,f.TYPE,f.BQ/f.BQAREA BQDH,f.BQBM,f.BQAREA,f.DOSAGE_TIME FROM (SELECT a.sort,a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,SUM(d1.AREA) BQAREA,d1.TYPE,d1.DOSAGE_TIME FROM (SELECT t.sort,t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.ORG_NAME,o.SORT,o.ID FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND ID = #{orgId}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid
AND vu.HEAT_TYPE=#{feedType}
