d.UNITNAME as unitName,
d.UNITTYPE as unitType,

    MAX((CASE d.DOSAGE_TIME WHEN #{tag} THEN d.DOSAGE/d.AREA ELSE 0 END)) c${index}

,

    MAX((CASE d.DOSAGE_TIME WHEN #{val} THEN d.DOSAGE/d.AREA ELSE 0 END)) l${i}

,

    0 p${i}

FROM (select a.unitid,a.unittype,a.unitname,m.DOSAGE as DOSAGE,m.DOSAGE_TIME as DOSAGE_TIME ,m.BM,m.AREA  FROM
(select UNITID,UNITNAME,UNITTYPE from v_emc_unit where 1=1

    and  HEAT_TYPE =#{feedType}

UNITTYPE = #{orgType}

    and FIND_IN_SET(ORGID,emc_func_org_getchilds(#{orgId}))

)a left join
( SELECT AVG(fdh.AREA) AREA,fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d') DOSAGE_TIME
FROM t_emc_final_data_hour_tj fdh,t_emc_energy_type eet  WHERE 1=1

    AND fdh.COMID= #{comId}


    and eet.TYPE = #{energytype,jdbcType=VARCHAR}



    and fdh.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}

and fdh.TYPEID = eet.ID
GROUP BY fdh.UNITID,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d')) m on a.unitid = m.unitid
union all
(select a.unitid,a.unittype,a.unitname,m.DOSAGE as DOSAGE,m.DOSAGE_TIME as DOSAGE_TIME ,m.BM,m.AREA  FROM
(select UNITID,UNITNAME,UNITTYPE from v_emc_unit where 1=1

    and  HEAT_TYPE =#{feedType}

HEAT_TYPE = #{orgType}

    and FIND_IN_SET(ORGID,emc_func_org_getchilds(#{orgId}))

)a left join
(SELECT AVG(fdh.AREA) AREA,fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d')
DOSAGE_TIME
FROM t_emc_final_data_hour_tj fdh,t_emc_energy_type eet WHERE fdh.TYPEID = eet.ID
AND fdh.COMID= #{comId} 
and eet.TYPE = #{energytype,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME >= #{startTimeTq,jdbcType=VARCHAR}


    and fdh.DOSAGE_TIME <= #{endTimeTq,jdbcType=VARCHAR}

GROUP BY fdh.UNITID,DATE_FORMAT(DOSAGE_TIME,'%Y-%m-%d'))m on a.unitid = m.unitid
)

) d   GROUP BY d.UNITID  order by d.UNITTYPE

SELECT (CASE WHEN M.UNITTYPE = '1' THEN '源' WHEN M.UNITTYPE = '2' THEN '网' WHEN M.UNITTYPE = '3' THEN '站' WHEN M.UNITTYPE = '4' THEN '线' WHEN M.UNITTYPE = '5' THEN '户' else '' end) as unitName, M.UNITTYPE as unitType,
ROUND(IFNULL(MAX((CASE M.DOSAGE_TIME WHEN #{tag} THEN M.DOSAGE/M.area ELSE 0 END)),0),2) c${index}
,
ROUND(IFNULL(MAX((CASE M.DOSAGE_TIME WHEN #{val} THEN M.DOSAGE/M.area ELSE 0 END)),0),2) l${i}
,
0 p${i}
FROM (SELECT ev.UNITTYPE, SUM(fdh.DOSAGE) DOSAGE, avg(fdh.area) area, SUM(fdh.DOSAGE * fdh.COAL_COEF) BM, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour_tj fdh, t_emc_energy_type eet, (select UNITID,UNITTYPE,COMID from v_emc_unit where 1=1
and EXISTS (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId})) and id = ORGID)
) ev WHERE fdh.TYPEID = eet.ID and fdh.UNITID = ev.UNITID and fdh.COMID = ev.COMID
AND fdh.COMID= #{comId}
and eet.TYPE = #{type,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME >= #{startTime,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME <= #{endTime,jdbcType=VARCHAR}
GROUP BY ev.UNITTYPE, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d')
UNION ALL (SELECT ev.UNITTYPE, avg(fdh.area) area, SUM(fdh.DOSAGE) DOSAGE, SUM(fdh.DOSAGE * fdh.COAL_COEF) BM, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d') DOSAGE_TIME FROM t_emc_final_data_hour_tj fdh, t_emc_energy_type eet, (select UNITID,UNITTYPE,COMID from v_emc_unit where 1=1
and EXISTS (select id from t_emc_org where FIND_IN_SET(id,emc_func_org_getchilds(#{orgId})) and id = ORGID)
) ev WHERE fdh.TYPEID = eet.ID and fdh.UNITID = ev.UNITID and fdh.COMID = ev.COMID
AND fdh.COMID= #{comId}
and eet.TYPE = #{type,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME >= #{startTimeTq,jdbcType=VARCHAR}
and fdh.DOSAGE_TIME <= #{endTimeTq,jdbcType=VARCHAR}
GROUP BY ev.UNITTYPE, DATE_FORMAT(DOSAGE_TIME, '%Y-%m-%d')))M
group by M.UNITTYPE

SELECT ROUND((IFNULL(d1.waterEnergy,0)),2) waterBq, ROUND((IFNULL(d1.electricEnergy,0)),2) electricBq, ROUND((IFNULL(d1.gasEnergy,0)),2) gasBq, ROUND((IFNULL(d1.hotEnergy,0)),2) hotBq, ROUND((IFNULL(d1.coalEnergy,0)),2) coalBq, ROUND((IFNULL(d2.waterEnergy,0)),2) waterTq, ROUND((IFNULL(d2.electricEnergy,0)),2) electricTq, ROUND((IFNULL(d2.gasEnergy,0)),2) gasTq, ROUND((IFNULL(d2.hotEnergy,0)),2) hotTq, ROUND((IFNULL(d2.coalEnergy,0)),2) coalTq,
d1.yeardate TIME from ( SELECT SUM( CASE a.energytype WHEN '1' THEN a.dosage / a.area ELSE 0 END ) AS waterEnergy, SUM( CASE a.energytype WHEN '2' THEN a.dosage / a.area ELSE 0 END ) AS electricEnergy, SUM( CASE a.energytype WHEN '3' THEN a.dosage / a.area ELSE 0 END ) AS gasEnergy, SUM( CASE a.energytype WHEN '4' THEN a.dosage / a.area ELSE 0 END ) AS hotEnergy, SUM( CASE a.energytype WHEN '5' THEN a.dosage / a.area ELSE 0 END ) AS coalEnergy, a.energytype, a.yeardate, a.total from ( SELECT sum(B.area) area, sum(B.dosage) dosage, SUM(B.total) total, B.energytype, a.unittype, B.yeardate FROM ( SELECT vu.UNITID,vu.UNITTYPE FROM ( t_emc_org LEFT JOIN v_emc_unit vu ON ID = vu.ORGID
AND vu.heat_type=#{feedType,jdbcType=VARCHAR}
AND vu.UNITTYPE = #{orgType,jdbcType=VARCHAR}
and ID=#{id}
) WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ) AND P_ORG_ID != '0' ) a LEFT JOIN ( SELECT h.UNITID AS unitid, avg(h.AREA) area, sum(h.DOSAGE) AS dosage, sum(h.DOSAGE * h.COAL_COEF) AS total, et.type AS energytype, DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') AS yeardate FROM ( ${tableName} h LEFT JOIN t_emc_energy_type et ON h.TYPEID = et.id ) WHERE 1 = 1 AND ( DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') >= DATE_FORMAT(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d') AND DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') <= DATE_FORMAT(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d') ) OR ( DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') >= SUBDATE( DATE_FORMAT(#{startTime,jdbcType=VARCHAR}, '%Y-%m-%d'), INTERVAL 1 YEAR ) AND DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') <= SUBDATE( DATE_FORMAT(#{endTime,jdbcType=VARCHAR}, '%Y-%m-%d'), INTERVAL 1 YEAR ) ) GROUP BY unitid, energytype, yeardate ) B ON a.UNITID = B.unitid GROUP BY B.energytype, B.yeardate )a WHERE a.yeardate IS NOT NULL GROUP BY a.yeardate )d1 LEFT JOIN ( SELECT SUM( CASE a.energytype WHEN '1' THEN a.dosage / a.area ELSE 0 END ) AS waterEnergy, SUM( CASE a.energytype WHEN '2' THEN a.dosage / a.area ELSE 0 END ) AS electricEnergy, SUM( CASE a.energytype WHEN '3' THEN a.dosage / a.area ELSE 0 END ) AS gasEnergy, SUM( CASE a.energytype WHEN '4' THEN a.dosage / a.area ELSE 0 END ) AS hotEnergy, SUM( CASE a.energytype WHEN '5' THEN a.dosage / a.area ELSE 0 END ) AS coalEnergy, a.energytype, a.yeardate, a.total from ( SELECT sum(B.area) area, sum(B.dosage) dosage, SUM(B.total) total, B.energytype, a.unittype, B.yeardate FROM ( SELECT vu.UNITID,vu.UNITTYPE FROM ( t_emc_org LEFT JOIN v_emc_unit vu ON ID = vu.ORGID
AND vu.heat_type=#{feedType,jdbcType=VARCHAR}
AND vu.UNITTYPE = #{orgType,jdbcType=VARCHAR}
and ID=#{id}
) WHERE FIND_IN_SET( ID, emc_func_org_getchilds (#{orgId,jdbcType=VARCHAR}) ) AND P_ORG_ID != '0' ) a LEFT JOIN ( SELECT h.UNITID AS unitid,
avg(h.AREA) area, sum(h.DOSAGE) AS dosage, sum(h.DOSAGE * h.COAL_COEF) AS total, et.type AS energytype, DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') AS yeardate FROM ( ${tableName} h LEFT JOIN t_emc_energy_type et ON h.TYPEID = et.id ) WHERE 1 = 1 AND ( DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') >= DATE_FORMAT(#{startTimeTq,jdbcType=VARCHAR}, '%Y-%m-%d') AND DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') <= DATE_FORMAT(#{endTimeTq,jdbcType=VARCHAR}, '%Y-%m-%d') ) OR ( DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') >= SUBDATE( DATE_FORMAT(#{startTimeTq,jdbcType=VARCHAR}, '%Y-%m-%d'), INTERVAL 1 YEAR ) AND DATE_FORMAT(h.DOSAGE_TIME, '%Y-%m-%d') <= SUBDATE( DATE_FORMAT(#{endTimeTq,jdbcType=VARCHAR}, '%Y-%m-%d'), INTERVAL 1 YEAR ) ) GROUP BY unitid, energytype, yeardate ) B ON a.UNITID = B.unitid GROUP BY B.energytype, B.yeardate )a WHERE a.yeardate IS NOT NULL GROUP BY a.yeardate )d2 on d1.energytype=d2.energytype

SELECT t.bqwater STotal, ROUND(IFNULL((t.bqwater-t.tqwater)/t.tqwater,0),2) STB, t.bqelectric DTotal, ROUND(IFNULL((t.bqelectric-t.tqelectric)/t.tqelectric,0),2) DTB, t.bqgas QTotal, ROUND(IFNULL((t.bqgas-t.tqgas)/t.tqgas,0),2) QTB, t.bqheat RTotal, ROUND(IFNULL((t.bqheat-t.tqheat)/t.tqheat,0),2) RTB, t.bqcoal MTotal, ROUND(IFNULL((t.bqcoal-t.tqcoal)/t.tqheat,0),2) MTB from ( SELECT v.fgsid id,v.ORG_NAME orgname,v.sort, ROUND(IFNULL(SUM(CASE v.TYPE WHEN '4' THEN 0 ELSE v.BQBM END)/AVG(v.BQAREA),0),2) BQDH, ROUND(IFNULL(SUM(CASE v1.TYPE WHEN '4' THEN 0 ELSE v1.BQBM END)/AVG(v1.BQAREA),0),2) TQDH, ROUND(MAX(CASE v.TYPE WHEN '1' THEN v.BQDH ELSE 0 END),2) bqwater, ROUND(MAX(CASE v1.TYPE WHEN '1' THEN v1.BQDH ELSE 0 END),2) tqwater, ROUND(MAX(CASE v.TYPE WHEN '2' THEN v.BQDH ELSE 0 END),2) bqelectric, ROUND(MAX(CASE v1.TYPE WHEN '2' THEN v1.BQDH ELSE 0 END),2) tqelectric, ROUND(MAX(CASE v.TYPE WHEN '3' THEN v.BQDH ELSE 0 END),2) bqgas, ROUND(MAX(CASE v1.TYPE WHEN '3' THEN v1.BQDH ELSE 0 END),2) tqgas, ROUND(MAX(CASE v.TYPE WHEN '4' THEN v.BQDH ELSE 0 END),2) bqheat, ROUND(MAX(CASE v1.TYPE WHEN '4' THEN v1.BQDH ELSE 0 END),2) tqheat, ROUND(MAX(CASE v.TYPE WHEN '5' THEN v.BQDH ELSE 0 END),2) bqcoal, ROUND(MAX(CASE v1.TYPE WHEN '5' THEN v1.BQDH ELSE 0 END),2) tqcoal, ROUND(MAX(CASE v.TYPE WHEN '6' THEN v.BQDH ELSE 0 END),2) bqoil, ROUND(MAX(CASE v1.TYPE WHEN '6' THEN v1.BQDH ELSE 0 END),2) tqoil FROM ( SELECT f.sort,f.fgsid,f.ORG_NAME,f.TYPE,f.BQ/f.BQAREA BQDH,f.BQBM,f.BQAREA FROM (SELECT a.sort,a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,SUM(d1.AREA) BQAREA,d1.TYPE FROM (SELECT t.sort,t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.ORG_NAME,o.SORT,o.ID FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND ID = #{id}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,AVG(fdh.AREA) AREA,eet.TYPE FROM ${tableName} fdh LEFT JOIN t_emc_energy_type eet ON eet.ID = fdh.TYPEID WHERE fdh.DOSAGE_TIME >= #{startTime} AND fdh.DOSAGE_TIME <= #{endTime} GROUP BY fdh.UNITID,eet.TYPE) d1 ON d1.UNITID = vu.unitid GROUP BY a.fgsid,a.ORG_NAME,d1.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v LEFT JOIN ( SELECT f.fgsid,f.ORG_NAME,f.TYPE,f.BQ/f.BQAREA BQDH,f.BQBM,f.BQAREA FROM (SELECT a.fgsid,a.ORG_NAME,SUM(d1.BM) BQBM,SUM(d1.DOSAGE) BQ,SUM(d1.AREA) BQAREA,d1.TYPE FROM (SELECT t.fgsid,tmo.ORG_NAME,t.ID orgid FROM ( SELECT ( SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = (SELECT ID FROM t_emc_org WHERE FIND_IN_SET( ID, emc_func_org_getparents (o.ID) ) AND P_ORG_ID = 0) ) fgsid, o.* FROM t_emc_org o WHERE o.COM_ID = #{comId}) t, (SELECT * FROM t_emc_org WHERE 1=1
AND ID = #{id}
) tmo WHERE tmo.ID = t.fgsid) a LEFT JOIN v_emc_unit vu ON vu.ORGID = a.orgid AND vu.HEAT_TYPE=#{feedType}
AND vu.UNITTYPE=#{orgType}
LEFT JOIN (SELECT fdh.UNITID,SUM(fdh.DOSAGE) DOSAGE,SUM(fdh.DOSAGE*fdh.COAL_COEF) BM,AVG(fdh.AREA) AREA,eet.TYPE FROM ${tableName} fdh LEFT JOIN t_emc_energy_type eet ON eet.ID = fdh.TYPEID WHERE fdh.DOSAGE_TIME >= #{startTimeTq} AND fdh.DOSAGE_TIME <= #{endTimeTq} GROUP BY fdh.UNITID,eet.TYPE) d1 ON d1.UNITID = vu.unitid GROUP BY a.fgsid,a.ORG_NAME,d1.TYPE) f GROUP BY f.fgsid,f.ORG_NAME,f.TYPE ) v1 ON v.fgsid = v1.fgsid AND v.TYPE = v1.TYPE GROUP BY v.fgsid,v.ORG_NAME ORDER BY v.SORT asc ) t
